where 1=1

  and id = #{id}


  and promoType = #{promoType}


  and hotelId = #{hotelId}


  and beginTime = #{beginTime}


  and endTime = #{endTime}


  and orderId = #{orderId}


  and onlinePaied = #{onlinePaied}


  and aliPaied = #{aliPaied}


  and wechatPaied = #{wechatPaied}


