mapper.update:
update b_bill_special_day


    and id = #{id},


    and promoType = #{promoType},


    and hotelId = #{hotelId},


    and beginTime = #{beginTime},


    and endTime = #{endTime},


    and orderId = #{orderId},


    and onlinePaied = #{onlinePaied},


    and aliPaied = #{aliPaied},


    and wechatPaied = #{wechatPaied},


    and billCost = #{billCost},


    and changeCost = #{changeCost},


    and finalCost = #{finalCost},


    and income = #{income},


    and createTime = #{createTime},


    and financeStatus = #{financeStatus},


    and availableMoney = #{availableMoney},


where 1=1

  and id = #{id}


  and promoType = #{promoType}


  and hotelId = #{hotelId}


  and beginTime = #{beginTime}


  and endTime = #{endTime}


  and orderId = #{orderId}


  and onlinePaied = #{onlinePaied}


  and aliPaied = #{aliPaied}


  and wechatPaied = #{wechatPaied}


  and billCost = #{billCost}


  and changeCost = #{changeCost}


  and finalCost = #{finalCost}


  and income = #{income}


  and createTime = #{createTime}


  and financeStatus = #{financeStatus}


  and availableMoney = #{availableMoney}

mapper.select:
select
  id,
  promoType,
  hotelId,
  beginTime,
  endTime,
  orderId,
  onlinePaied,
  aliPaied,
  wechatPaied,
  billCost,
  changeCost,
  finalCost,
  income,
  createTime,
  financeStatus,
  availableMoney
  from b_bill_special_day t
  where 1=1

  and id = #{id}


  and promoType = #{promoType}


  and hotelId = #{hotelId}


  and beginTime = #{beginTime}


  and endTime = #{endTime}


  and orderId = #{orderId}


  and onlinePaied = #{onlinePaied}


  and aliPaied = #{aliPaied}


  and wechatPaied = #{wechatPaied}


  and billCost = #{billCost}


  and changeCost = #{changeCost}


  and finalCost = #{finalCost}


  and income = #{income}


  and createTime = #{createTime}


  and financeStatus = #{financeStatus}


  and availableMoney = #{availableMoney}
