mapper.update:
update b_promotion set name = #{name,jdbcType=VARCHAR}, Createtime = #{createtime,jdbcType=TIMESTAMP}, Begintime = #{begintime,jdbcType=TIMESTAMP}, Endtime = #{endtime,jdbcType=TIMESTAMP}, type = #{type,jdbcType=INTEGER}, isticket = #{isticket,jdbcType=CHAR}, num = #{num,jdbcType=INTEGER}, classname = #{classname,jdbcType=VARCHAR}, isota = #{isota,jdbcType=CHAR}, otapre = #{otapre,jdbcType=DECIMAL}, activitiesid = #{activitiesid,jdbcType=BIGINT}, pic = #{pic,jdbcType=VARCHAR}, version = #{version,jdbcType=BIGINT}, info = #{info,jdbcType=LONGVARCHAR}, note = #{note,jdbcType=LONGVARCHAR}, isinstance = #{isinstance,jdbcType=CHAR}, weight = #{weight,jdbcType=BIGINT},
totalnum = #{totalnum,jdbcType=BIGINT}, plannum = #{plannum,jdbcType=BIGINT}, protype = #{protype,jdbcType=INTEGER}, onlineprice = #{onlineprice,jdbcType=DECIMAL}, offlineprice = #{offlineprice,jdbcType=DECIMAL},
expiretype = #{expiretype,jdbcType=INTEGER}, expiredaynum = #{expiredaynum,jdbcType=INTEGER}, effectivetype = #{effectivetype,jdbcType=INTEGER}, platformtype = #{platformtype,jdbcType=INTEGER}, sourcecdkey = #{sourcecdkey,jdbcType=VARCHAR}, channelid = #{channelid,jdbcType=BIGINT} where id = #{id,jdbcType=BIGINT}

mapper.select:
select c.* from ( select id as otaorderid from b_otaorder where mid = #{mid} and orderstatus != 510 and orderstatus != 511 and orderstatus != 512 and orderstatus != 513 and spreaduser is not null
and id = #{otaorderid,jdbcType=LONGVARCHAR}
) a, b_promotion_price b, (select * from b_promotion where isinstance = 'T' and type = 2) c where a.otaorderid = b.otaorderid and b.promotion = c.id
