sql.where:
            a.isBack ='F'

            
                AND  a.id = #{id}
            
            
                AND a.roomTypeId = #{roomTypeId}
            
            
                AND a.oldRoomTypeId = #{oldRoomTypeId}
            
            
                AND a.roomNo = #{roomNo}
            
            
            AND a.pms = #{pms}
            
            
                AND a.saleName = #{saleName}
            
            
                AND a.saleType = #{saleType}
            
            
                AND a.createDate = #{createDate}
            
            
                AND a.isBack = #{isBack}
            
            
                AND a.hotelId = #{hotelId}
            
            
                AND a.roomId = #{roomId}

mapper.select:
SELECT saletype as promotype, costprice as promopice
FROM t_room_sale_config
where hotelId = #{hotelId}

mapper.update:
update t_room_sale
set isBack =  #{isBack}
where id = #{id}
